import React, {useRef, useEffect, useState} from 'react'
import {gsap, TweenMax, Power3} from 'gsap'
import Navigation from '../components/Navigation.js'
import Footer from '../components/Footer.js'
import '../css/About.css'

function About() {
  let graphContainer = useRef(null)
  let div1 = useRef(null)
  let div2 = useRef(null)
  let div3 = useRef(null)
  let div4 = useRef(null)

  useEffect(() => {
    TweenMax.to(graphContainer, 0, {css: {visibility:'visible'}})

    TweenMax.staggerFrom(
      [div1,div2,div3,div4], 0.8, {opacity: 0, y: 100, ease: Power3.easeOut}, .2 )
  }, [])

    return (
        <div>
          <Navigation />
          <div className="about-container" ref={el => graphContainer = el}>

            <div className="intro-container">
              <div>
              <h2>I'm a Full-Stack Developer based in San Francisco, California</h2>
              <p></p>
              </div>
              <div></div>
            </div>

            <div className="graph-container">
              <div className="div1"></div>
              <div className="div2"></div>
              <div className="div3"></div>
              <div className="div4"></div>
            </div>
          </div>
          <Footer />
        </div>
    )

}
export default About;
